import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Logout = () => {
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.clear();
    navigate("/");
    window.location.reload(); // optional: force refresh
  }, [navigate]);

  return <p>Logging out...</p>;
};

export default Logout;
