import React, { useState } from 'react';
import axios from 'axios';

const AddDeduction = ({ employeeId }) => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [responseMsg, setResponseMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      await axios.post(
        'http://localhost:8000/api/deduction/create/',
        {
          employee: employeeId,
          amount: parseFloat(amount),
          description,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setResponseMsg('Deduction added successfully.');
      setAmount('');
      setDescription('');
    } catch (error) {
      const errMsg = error.response?.data?.error || 'Failed to add deduction.';
      setResponseMsg(errMsg);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {responseMsg && <p style={{ color: 'green' }}>{responseMsg}</p>}
      <div style={{ marginBottom: '10px' }}>
        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <button type="submit">Add Deduction</button>
    </form>
  );
};

export default AddDeduction;
