import React, { useState } from 'react';
import axios from 'axios';

const AddAllowance = ({ employeeId }) => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [responseMsg, setResponseMsg] = useState('');

  const token = localStorage.getItem('token');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        'http://localhost:8000/api/salary/add-allowance/',
        {
          employee: employeeId,
          amount: parseFloat(amount),
          description,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setResponseMsg('Allowance added successfully!');
      setAmount('');
      setDescription('');
    } catch (error) {
      setResponseMsg('Failed to add allowance.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '400px', margin: 'auto' }}>
      {responseMsg && (
        <p style={{ color: 'green', textAlign: 'center', marginBottom: '10px' }}>{responseMsg}</p>
      )}
      <div style={{ marginBottom: '16px' }}>
        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          style={{ width: '100%', padding: '8px', marginTop: '4px' }}
        />
      </div>
      <div style={{ marginBottom: '16px' }}>
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{ width: '100%', padding: '8px', marginTop: '4px' }}
        />
      </div>
      <button
        type="submit"
        style={{
          backgroundColor: '#1976d2',
          color: '#fff',
          padding: '10px 20px',
          border: 'none',
          cursor: 'pointer',
          borderRadius: '4px',
          width: '100%',
        }}
      >
        Add Allowance
      </button>
    </form>
  );
};

export default AddAllowance;
