import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './EmployeeList.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [salaryStatus, setSalaryStatus] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/users/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(response.data);

        const statusObj = {};
        await Promise.all(
          response.data.map(async (emp) => {
            try {
              const salaryRes = await axios.get(
                `http://localhost:8000/api/salary/get_current_salary_details/${emp.id}/`,
                { headers: { Authorization: `Bearer ${token}` } }
              );
              statusObj[emp.id] = salaryRes.data;
            } catch {
              statusObj[emp.id] = null;
            }
          })
        );
        setSalaryStatus(statusObj);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  return (
    <div className="employee-list-container">
      <h2>Employee List</h2>
      <table className="employee-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Salary Actions</th>
            <th>Salary History</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp.id}>
              <td>
                <a href={`/employees/${emp.id}`}>
                  {emp.first_name} {emp.last_name}
                </a>
              </td>
              <td>{emp.email}</td>
              <td>{emp.is_active ? 'Active' : 'Inactive'}</td>
              <td>
                {salaryStatus[emp.id] ? (
                  salaryStatus[emp.id].is_paid === false ? (
                    <button
                      className="generate-slip-button"
                      onClick={() => navigate(`/salary/generate/${emp.id}`)}
                    >
                      Generate Slip
                    </button>
                  ) : (
                    'Paid'
                  )
                ) : (
                  <button
                    className="add-salary-button"
                    onClick={() => navigate(`/salary/add/${emp.id}`)}
                  >
                    Add Salary
                  </button>
                )}
              </td>
              <td>
                <button
                  className="history-button"
                  onClick={() => navigate(`/salary/history/${emp.id}`)}
                >
                  Salary History
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
