import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AddSalary = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const [salaryData, setSalaryData] = useState({
    basic_salary: '',
    allowances: '',
    deductions: '',
  });

  useEffect(() => {
    const fetchEmployee = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get(`http://localhost:8000/api/user/${id}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployee(res.data);
      } catch (err) {
        console.error('Error:', err);
      }
    };
    fetchEmployee();
  }, [id]);

  const handleChange = (e) => {
    setSalaryData({ ...salaryData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.post('http://localhost:8000/api/salary/create/', {
        employee: id,
        ...salaryData,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Salary added successfully!');
    } catch (err) {
      alert('Failed to add salary: ' + (err.response?.data?.error || 'Something went wrong.'));
    }
  };

  return (
    <div className="employee-list-container">
      <h2>Add Salary</h2>
      {employee ? (
        <>
          <h3>{employee.first_name} {employee.last_name}</h3>
          <form onSubmit={handleSubmit}>
            <label>Basic Salary:</label>
            <input type="number" name="basic_salary" onChange={handleChange} required />

            <label>Allowances:</label>
            <input type="number" name="allowances" onChange={handleChange} required />

            <label>Deductions:</label>
            <input type="number" name="deductions" onChange={handleChange} required />

            <br />
            <button type="submit">Submit</button>
          </form>
        </>
      ) : (
        <p>Loading employee info...</p>
      )}
    </div>
  );
};

export default AddSalary;
