import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddSalary = () => {
  const { employeeId } = useParams();
  const navigate = useNavigate();
  const [employee, setEmployee] = useState(null);
  const [formData, setFormData] = useState({
    basic_salary: '',
    allowances: '',
    deductions: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchEmployee = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get(`http://localhost:8000/api/user/${employeeId}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEmployee(res.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load employee details.');
        setLoading(false);
        console.error(err);
      }
    };

    fetchEmployee();
  }, [employeeId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const payload = {
      ...formData,
      employee: employeeId, // Backend expects "employee"
    };

    try {
       await axios.post(`http://localhost:8000/api/salary/create/`, payload, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Salary created successfully!');
      navigate('/employees'); // Redirect back to employee list
    } catch (err) {
      console.error(err);
      const errMsg =
        err.response?.data?.error ||
        'Failed to create salary. It may already exist.';
      alert(errMsg);
    }
  };

  if (loading) return <h3>Loading employee info...</h3>;
  if (error) return <h3>{error}</h3>;

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h2>Add Salary</h2>
      <p><strong>Employee:</strong> {employee.first_name} {employee.last_name}</p>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Basic Salary:</label><br />
          <input
            type="number"
            name="basic_salary"
            value={formData.basic_salary}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Allowances:</label><br />
          <input
            type="number"
            name="allowances"
            value={formData.allowances}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Deductions:</label><br />
          <input
            type="number"
            name="deductions"
            value={formData.deductions}
            onChange={handleChange}
            required
          />
        </div>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddSalary;
