import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';

const PendingRequests = () => {
  const [pendingLeaves, setPendingLeaves] = useState([]);
  const [employees, setEmployees] = useState({});
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  // Function to fetch employee first name based on employee ID
  const fetchEmployeeFirstName = useCallback(async (employeeId) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/employee/${employeeId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      console.log('Employee response:', response.data); // Log the response for debugging

      if (response.data && response.data.first_name) {
        setEmployees((prevState) => ({
          ...prevState,
          [employeeId]: response.data.first_name,  // Store the first name with employee_id as the key
        }));
      } else {
        setEmployees((prevState) => ({
          ...prevState,
          [employeeId]: 'No first name found',  // Fallback if no first name is found
        }));
      }

    } catch (err) {
      console.error('Error fetching employee first name:', err);
      setError('Failed to fetch employee first name.');
      setEmployees((prevState) => ({
        ...prevState,
        [employeeId]: 'Error fetching first name',  // Fallback error message
      }));
    }
  }, [token]);

  useEffect(() => {
    const fetchPendingLeaves = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/leave/pending/', {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });
        setPendingLeaves(response.data);

        // Fetch employee first name for each pending leave request
        response.data.forEach((leave) => {
          if (!employees[leave.employee_id]) {
            fetchEmployeeFirstName(leave.employee_id);
          }
        });
      } catch (err) {
        console.error(err);
        setError('Failed to fetch pending leave requests. Please try again later.');
      }
    };

    fetchPendingLeaves();
  }, [token, fetchEmployeeFirstName, employees]);

  const handleApprove = async (leaveId) => {
    try {
      await axios.patch(
        `http://localhost:8000/api/leave/approve/${leaveId}/`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setPendingLeaves((prevState) =>
        prevState.filter((leave) => leave.id !== leaveId)
      );
    } catch (err) {
      console.error(err);
      setError('Error approving leave request.');
    }
  };

  const handleReject = async (leaveId) => {
    try {
      await axios.patch(
        `http://localhost:8000/api/leave/reject/${leaveId}/`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setPendingLeaves((prevState) =>
        prevState.filter((leave) => leave.id !== leaveId)
      );
    } catch (err) {
      console.error(err);
      setError('Error rejecting leave request.');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-semibold mb-4">Pending Leave Requests</h2>
      {error && <p className="text-red-500">{error}</p>}

      {pendingLeaves.length === 0 ? (
        <p>No pending leave requests.</p>
      ) : (
        <table className="table-auto w-full border-collapse">
          <thead>
            <tr>
              <th className="border-b px-4 py-2">Employee ID</th>
              <th className="border-b px-4 py-2">Employee First Name</th>
              <th className="border-b px-4 py-2">Leave Type</th>
              <th className="border-b px-4 py-2">Start Date</th>
              <th className="border-b px-4 py-2">End Date</th>
              <th className="border-b px-4 py-2">Status</th>
              <th className="border-b px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingLeaves.map((leave) => (
              <tr key={leave.id}>
                <td className="border-b px-4 py-2">{leave.employee_id}</td>
                <td className="border-b px-4 py-2">
                  {employees[leave.employee_id] || 'Loading...'}
                </td>
                <td className="border-b px-4 py-2">{leave.leave_type}</td>
                <td className="border-b px-4 py-2">{leave.start_date}</td>
                <td className="border-b px-4 py-2">{leave.end_date}</td>
                <td className="border-b px-4 py-2">{leave.status}</td>
                <td className="border-b px-4 py-2">
                  <button
                    onClick={() => handleApprove(leave.id)}
                    className="bg-green-500 text-white px-4 py-2 rounded mr-2"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleReject(leave.id)}
                    className="bg-red-500 text-white px-4 py-2 rounded"
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PendingRequests;
