import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const HRDashboard = () => {
  const [employeeCount, setEmployeeCount] = useState(0);
  const [pendingLeaves, setPendingLeaves] = useState(0);
  const [payrollStatus, setPayrollStatus] = useState("");

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem("token");

        const employeesRes = await axios.get("http://127.0.0.1:8000/api//users/", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const leavesRes = await axios.get("http://127.0.0.1:8000/api/leave/pending/", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const payrollRes = await axios.get("http://127.0.0.1:8000/api/salary/history/", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setEmployeeCount(employeesRes.data.count);
        setPendingLeaves(leavesRes.data.count);
        setPayrollStatus(payrollRes.data.status);
      } catch (error) {
        console.error("Error fetching dashboard data", error);
      }
    };

    fetchDashboardData();
  }, []);

  return (
    <div className="dashboard">
      <h2>HR Dashboard</h2>
      <div className="dashboard-cards">
        <div className="card">
          <h3>Total Employees</h3>
          <p>{employeeCount}</p>
        </div>
        <div className="card">
          <h3>Pending Leave Requests</h3>
          <p>{pendingLeaves}</p>
        </div>
        <div className="card">
          <h3>Payroll Status</h3>
          <p>{payrollStatus}</p>
        </div>
      </div>
    </div>
  );
};

export default HRDashboard;
