import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EmployeeList.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [salaryMap, setSalaryMap] = useState({});
  const navigate = useNavigate();

  const fetchEmployees = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/user/', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setEmployees(response.data);

      // Fetch salary records for current month
      const salaryResponse = await axios.get(
        'http://127.0.0.1:8000/api/salary/current_month_salaries/',
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const map = {};
      salaryResponse.data.forEach((record) => {
        map[record.employee_id] = {
          is_paid: record.salary_status === 'Paid',
          ...record,
        };
      });
      setSalaryMap(map);
    } catch (error) {
      console.error('Error fetching employees or salaries:', error);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  useEffect(() => {
    const handleFocus = () => fetchEmployees();
    window.addEventListener('focus', handleFocus);
    return () => window.removeEventListener('focus', handleFocus);
  }, []);

  const renderActionButton = (empId) => {
    const record = salaryMap[empId];

    if (!record) {
      return (
        <button
          className="small-button"
          onClick={() => navigate(`/salary/add/${empId}`)}
        >
          Add Salary
        </button>
      );
    }

    if (record.is_paid) {
      return null;
    }

    return (
      <div className="button-group">
        <button
          className="small-button"
          onClick={() => navigate(`/salary/add-allowance/${empId}`)}
        >
          Add Allowance
        </button>
        <button
          className="small-button"
          onClick={() => navigate(`/salary/add-deduction/${empId}`)}
        >
          Add Deduction
        </button>
        <button
          className="small-button"
          onClick={() => navigate(`/salary/history/${empId}`)}
        >
          Generate Payslip
        </button>
      </div>
    );
  };

  return (
    <div className="employee-list-container">
      <h2>Employee List</h2>
      <table className="employee-list-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Salary Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp.id}>
              <td>{emp.name}</td>
              <td>{emp.email}</td>
              <td>
                {salaryMap[emp.id]?.is_paid ? 'Paid' : 'Not Paid'}
              </td>
              <td>{renderActionButton(emp.id)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
