import React, { useState } from 'react';
import axios from 'axios';

const deductionOptions = [
  'TDS',
  'PF',
  'INSURANCE',
  'LOP',
  'OTHER',
];

const AddDeduction = ({ employeeId }) => {
  const [amount, setAmount] = useState('');
  const [deductionType, setDeductionType] = useState('');
  const [responseMsg, setResponseMsg] = useState('');

  const token = localStorage.getItem('token');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        'http://localhost:8000/api/salary/add-deduction/',
        {
          employee: employeeId,
          amount: parseFloat(amount),
          deduction_type: deductionType,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setResponseMsg('✅ Deduction added successfully!');
      setAmount('');
      setDeductionType('');
    } catch (error) {
      setResponseMsg('❌ Failed to add deduction.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '400px', margin: 'auto' }}>
      {responseMsg && (
        <p style={{ color: 'green', textAlign: 'center', marginBottom: '10px' }}>{responseMsg}</p>
      )}
      <div style={{ marginBottom: '16px' }}>
        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          style={{ width: '100%', padding: '8px', marginTop: '4px' }}
        />
      </div>
      <div style={{ marginBottom: '16px' }}>
        <label>Deduction Type:</label>
        <select
          value={deductionType}
          onChange={(e) => setDeductionType(e.target.value)}
          required
          style={{ width: '100%', padding: '8px', marginTop: '4px' }}
        >
          <option value="">-- Select Type --</option>
          {deductionOptions.map((option) => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      </div>
      <button
        type="submit"
        style={{
          backgroundColor: '#d32f2f',
          color: '#fff',
          padding: '10px 20px',
          border: 'none',
          cursor: 'pointer',
          borderRadius: '4px',
          width: '100%',
        }}
      >
        Add Deduction
      </button>
    </form>
  );
};

export default AddDeduction;
