import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Login from "./pages/Auth/Login";
import EmployeeDashboard from "./pages/Dashboard/EmployeeDashboard";
import HRDashboard from "./pages/Dashboard/HRDashboard";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [role, setRole] = useState(localStorage.getItem("role"));

  useEffect(() => {
    setToken(localStorage.getItem("token"));
    setRole(localStorage.getItem("role"));
  }, []);

  return (
    <Router>
      <Routes>
        {/* If not logged in, show Login page */}
        <Route path="/" element={!token ? <Login setToken={setToken} setRole={setRole} /> : <Navigate to="/dashboard" />} />
        <Route path="/login" element={<Login setToken={setToken} setRole={setRole} />} />

        {/* Corrected role-based redirection */}
        <Route
          path="/dashboard"
          element={
            role === "HR"
              ? <Navigate to="/dashboard/hr" />
              : role === "Employee"
                ? <Navigate to="/dashboard/employee" />
                : <Navigate to="/login" />
          }
        />

        {/* Dashboard Routes */}
        <Route path="/dashboard/hr" element={role === "HR" ? <HRDashboard /> : <Navigate to="/login" />} />
        <Route path="/dashboard/employee" element={role === "Employee" ? <EmployeeDashboard /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
