import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const AddSalary = () => {
  const { employeeId } = useParams();
  const [employee, setEmployee] = useState(null);
  const [formData, setFormData] = useState({
    basic_salary: '',
    allowances: '',
    deductions: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployee = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get(`http://localhost:8000/api/user/${employeeId}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployee(response.data);
      } catch (error) {
        console.error('Error fetching employee:', error);
      }
    };

    fetchEmployee();
  }, [employeeId]);

  const handleChange = (e) => {
    setFormData((prevData) => ({
      ...prevData,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      const payload = {
        employee: employeeId,
        basic_salary: formData.basic_salary,
        allowances: formData.allowances,
        deductions: formData.deductions,
      };

      const response = await axios.post('http://localhost:8000/api/salary/create/', payload, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Salary added successfully!');
      navigate('/employees');
    } catch (error) {
      console.error('Error adding salary:', error);
      alert(error.response?.data?.error || 'Something went wrong!');
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: 'auto', padding: '20px' }}>
      <h2>Add Salary</h2>
      {employee ? (
        <>
          <p>
            <strong>Employee:</strong> {employee.first_name} {employee.last_name}
          </p>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Basic Salary:</label>
              <input
                type="number"
                name="basic_salary"
                value={formData.basic_salary}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Allowances:</label>
              <input
                type="number"
                name="allowances"
                value={formData.allowances}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Deductions:</label>
              <input
                type="number"
                name="deductions"
                value={formData.deductions}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" style={{ marginTop: '10px' }}>
              Submit
            </button>
          </form>
        </>
      ) : (
        <p>Loading employee info...</p>
      )}
    </div>
  );
};

export default AddSalary;
