import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EmployeeList.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [salaryData, setSalaryData] = useState({
    basic_salary: '',
    allowances: '',
    deductions: '',
  });

  useEffect(() => {
    const fetchEmployees = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get('http://localhost:8000/api/users/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Failed to fetch employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleInputChange = (e) => {
    setSalaryData({ ...salaryData, [e.target.name]: e.target.value });
  };

  const openSalaryForm = (employee) => {
    setSelectedEmployee(employee);
    setSalaryData({ basic_salary: '', allowances: '', deductions: '' });
    setShowForm(true);
  };

  const closeForm = () => {
    setShowForm(false);
    setSelectedEmployee(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const payload = {
      employee: selectedEmployee.id,
      ...salaryData,
    };

    try {
      const response = await axios.post('http://localhost:8000/api/salary/create/', payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Salary created successfully!');
      closeForm();
    } catch (error) {
      alert('Error creating salary: ' + (error.response?.data?.error || 'Something went wrong'));
    }
  };

  return (
    <div className="employee-list-container">
      <h2>Employee List</h2>
      <table className="employee-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Add Salary</th>
          </tr>
        </thead>
        <tbody>
          {employees.length > 0 ? (
            employees.map((emp) => (
              <tr key={emp.id}>
                <td>
                  <a href={`/employees/${emp.id}`}>
                    {emp.first_name} {emp.last_name}
                  </a>
                </td>
                <td>{emp.email}</td>
                <td>{emp.is_active ? 'Active' : 'Inactive'}</td>
                <td>
                  <button className="add-salary-button" onClick={() => openSalaryForm(emp)}>
                    Add Salary
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>
                No employees found.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {showForm && (
        <div className="salary-form-popup">
          <div className="salary-form-content">
            <h3>Add Salary for {selectedEmployee.first_name} {selectedEmployee.last_name}</h3>
            <form onSubmit={handleSubmit}>
              <label>Basic Salary</label>
              <input
                type="number"
                name="basic_salary"
                value={salaryData.basic_salary}
                onChange={handleInputChange}
                required
              />

              <label>Allowances</label>
              <input
                type="number"
                name="allowances"
                value={salaryData.allowances}
                onChange={handleInputChange}
                required
              />

              <label>Deductions</label>
              <input
                type="number"
                name="deductions"
                value={salaryData.deductions}
                onChange={handleInputChange}
                required
              />

              <div className="form-actions">
                <button type="submit">Submit</button>
                <button type="button" onClick={closeForm} className="cancel-button">
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeeList;
