import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Profile.css';

const Profile = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [message, setMessage] = useState('');

  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    if (!userId) {
      setMessage("User not logged in. Please login first.");
      return;
    }

    axios.get(`/api/users/${userId}/`)
      .then((res) => {
        setUserDetails(res.data);
      })
      .catch((err) => {
        console.error('Error fetching user profile:', err);
        setMessage('Failed to load profile.');
      });
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = (e) => {
    e.preventDefault();

    axios.patch(`/api/update-user/${userId}/`, userDetails)
      .then(() => {
        setMessage('Profile updated successfully!');
        setEditMode(false);
      })
      .catch((err) => {
        console.error('Update failed:', err);
        setMessage('Failed to update profile.');
      });
  };

  if (!userId) return <p className="message error">User not logged in. Please login.</p>;
  if (!userDetails) return <p>Loading profile...</p>;

  return (
    <div className="profile-container">
      <h2>My Profile</h2>

      {editMode ? (
        <form onSubmit={handleUpdate} className="profile-form">
          <label>First Name:</label>
          <input type="text" name="first_name" value={userDetails.first_name} onChange={handleChange} required />

          <label>Last Name:</label>
          <input type="text" name="last_name" value={userDetails.last_name} onChange={handleChange} required />

          <label>Email:</label>
          <input type="email" name="email" value={userDetails.email} onChange={handleChange} required />

          <label>Phone No:</label>
          <input type="text" name="phone_no" value={userDetails.phone_no} onChange={handleChange} required />

          <label>Designation:</label>
          <input type="text" name="designation" value={userDetails.designation} onChange={handleChange} required />

          <label>Department:</label>
          <input type="text" name="department" value={userDetails.department} onChange={handleChange} required />

          <label>Address:</label>
          <textarea name="address" value={userDetails.address} onChange={handleChange} required />

          <div className="btn-group">
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEditMode(false)} className="cancel-btn">Cancel</button>
          </div>
        </form>
      ) : (
        <div className="profile-view">
          <p><strong>Name:</strong> {userDetails.first_name} {userDetails.last_name}</p>
          <p><strong>Email:</strong> {userDetails.email}</p>
          <p><strong>Phone:</strong> {userDetails.phone_no}</p>
          <p><strong>Designation:</strong> {userDetails.designation}</p>
          <p><strong>Department:</strong> {userDetails.department}</p>
          <p><strong>Address:</strong> {userDetails.address}</p>
          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      )}

      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Profile;
