import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SalaryDetails = () => {
  const [salaryDetails, setSalaryDetails] = useState(null);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    // Fetch salary details when the component is mounted
    const fetchSalaryDetails = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/salary/current_salary_details/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.status === 200) {
          setSalaryDetails(response.data);
        }
      } catch (err) {
        console.error('Error fetching salary details:', err);
        setError('Failed to fetch salary details. Please try again later.');
      }
    };

    fetchSalaryDetails();
  }, [token]);

  return (
    <div className="salary-details">
      <h2>Salary Details</h2>
      {error && <p className="text-red-500">{error}</p>}
      {salaryDetails ? (
        <div className="salary-info">
          <p><strong>Employee Name:</strong> {salaryDetails.employee_name}</p>
          <p><strong>Current Salary:</strong> â‚¹{salaryDetails.current_salary}</p>
          <p><strong>Pay Period:</strong> {salaryDetails.pay_period}</p>
          <p><strong>Designation:</strong> {salaryDetails.designation}</p>
          <p><strong>Joining Date:</strong> {new Date(salaryDetails.joining_date).toLocaleDateString()}</p>
        </div>
      ) : (
        <p>Loading salary details...</p>
      )}
    </div>
  );
};

export default SalaryDetails;
