import axios from 'axios';
import './HRProfile.css'; // ðŸ‘ˆ import the CSS here

const HRProfile = () => {
  const [userDetails, setUserDetails] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/user/details/')
      .then(response => setUserDetails(response.data))
      .catch(error => setMessage('Failed to fetch details'));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserDetails({ ...userDetails, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.patch(`/api/update-user/${userDetails.id}/`, userDetails)
      .then(response => setMessage('Profile updated successfully!'))
      .catch(error => setMessage('Failed to update profile'));
  };

  return (
    <div className="hr-profile-container">
      <h2>Update Profile</h2>
      <form onSubmit={handleSubmit} className="hr-profile-form">
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={userDetails.name}
          onChange={handleChange}
        />
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={userDetails.email}
          onChange={handleChange}
        />
        <label>Phone:</label>
        <input
          type="text"
          name="phone"
          value={userDetails.phone}
          onChange={handleChange}
        />
        <label>Address:</label>
        <input
          type="text"
          name="address"
          value={userDetails.address}
          onChange={handleChange}
        />
        <button type="submit">Update Profile</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default HRProfile;
