import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AddAllowance.css';

const AddAllowance = () => {
  const { empId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const [allowanceType, setAllowanceType] = useState('');
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(null);

  const allowanceOptions = [
    'Bonus',
    'Overtime',
    'Travel',
    'House Rent',
    'Daily Allowance',
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `http://localhost:8000/api/allowance/create/`,
        {
          employee: empId,
          allowance_type: allowanceType,
          amount,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage('✅ Allowance added successfully');
      setIsSuccess(true);
      setTimeout(() => navigate('/employees'), 1500);
    } catch (err) {
      const errorMsg = err.response?.data?.error || '❌ Failed to add allowance';
      setMessage(errorMsg);
      setIsSuccess(false);
    }
  };

  return (
    <div className="form-container">
      <h2>Add Allowance</h2>

      {message && (
        <div className={`message ${isSuccess ? 'success' : 'error'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>Allowance Type:</label>
        <select
          value={allowanceType}
          onChange={(e) => setAllowanceType(e.target.value)}
          required
        >
          <option value="">-- Select Type --</option>
          {allowanceOptions.map((type) => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>

        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddAllowance;
