import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const SalaryDetailOfEmp = () => {
  const { id } = useParams();
  const [salaryHistory, setSalaryHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');

    const fetchSalaryHistory = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/api/salary/get_employee_payroll_history/${id}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSalaryHistory(res.data);
      } catch (error) {
        console.error('Failed to fetch salary history', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSalaryHistory();
  }, [id]);

  return (
    <div className="salary-history-container">
      <h2>Salary History</h2>
      {loading ? (
        <p>Loading...</p>
      ) : salaryHistory.length === 0 ? (
        <p>No salary records found for this employee.</p>
      ) : (
        <table className="salary-history-table">
          <thead>
            <tr>
              <th>Month</th>
              <th>Year</th>
              <th>Basic</th>
              <th>HRA</th>
              <th>Allowances</th>
              <th>Deductions</th>
              <th>Net Salary</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {salaryHistory.map((record, index) => (
              <tr key={index}>
                <td>{record.month}</td>
                <td>{record.year}</td>
                <td>{record.basic}</td>
                <td>{record.hra}</td>
                <td>{record.allowances}</td>
                <td>{record.deductions}</td>
                <td>{record.net_salary}</td>
                <td>{record.is_paid ? 'Paid' : 'Pending'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SalaryDetailOfEmp;
